        $('body').off('keyup.modal-close');      }      modal = $('<div>').css({        background: 'url(' + src + ') no-repeat center',        backgroundImage:'url('+src+')',        width: '100%',        height: '100%',        position: 'fixed',        zIndex: '1000000',        top: '0',        left: '0',        cursor: 'zoom-out'      }).click(function() {        removeModal();      }).appendTo('body');      modal.css('background-image','url('+src+')');      modal.attr('style','color:blue;width:100%;height:100%;background-image:url("'+src+'");background-repeat:no-repeat;background-position:center;background-color:RGBA(0,0,0,.5);position:fixed;top:0;left:0;cursor:zoom-out;background-size:contain;');      //handling ESC      $('body').on('keyup.modal-close', function(e) {        if (e.key === 'Escape') {          removeModal();        }      });    });    $(".owl-carousel").owlCarousel(            {              items: 2,              itemsDesktop : [1000,2], // 2 items between 1000px and 901px              itemsDesktopSmall : [900,2], // betweem 900px and 601px              itemsTablet: [700,1], // 2 items between 600 and 480              itemsMobile : [479,1] , // 1 item between 479 and 0              navigation: true,              lazyLoad: true,              pagination: false,              scrollPerPage : true,              navigationText : ["geri","ileri"],              navigationColor: "#171d26"            }    );  </script></body></html>